import React, { useState, useEffect } from "react";
import axios from "axios"




const Home = () => {
    const [breweries, setBreweries] = useState([]);
    useEffect(() => {
        axios
          .get(`https://api.openbrewerydb.org/breweries`)
          .then(res => {
            const charArr = res.name;
            console.log(charArr);
            setBreweries(charArr);
            console.log(charArr);
          })
          .catch(error => {
            console.log("data not returned", error);
          });
      }, []);

  return (
    <div className="App">
      <form>
        <label>
          Username:
          <input type="text" />
        </label>
      </form>
    </div>
  );
};

export default Home;
