import React, { useState, useEffect } from "react";
import axios from "axios";

const Home = () => {
  const [breweries, setBrewery] = useState([]);
  useEffect(() => {
    // change ^^^ that line and grab the id from the URL
    // You will NEED to add a dependency array to this effect hook

    axios
      .get(`https://api.openbrewerydb.org/breweries`)
      .then(response => {
        console.log(response.data);
        setBrewery(response.data);
      })

      .catch(error => {
        console.error(error);
      });
  }, []);

  return (
    <div className="brewerie-list">
      return (
    <form onSubmit={e => onSubmitHandler(e)} className="search-bar">
      <input
        onChange={e => setBrewery(e.target.value)}
        placeholder="title of brewery"
      />
      <button type="submit" disabled={!breweries}>
        Search
      </button>
    </form>
      <h1>Search Breweries</h1>
      {breweries.map(name => (
        <div>
          <h3>Brewery Name</h3>
          {name.name}
          <h3>Brewery Type</h3>
          {name.brewery_type}
        </div>
      ))}
    </div>
  );
};

export default Home;
