import React, { useState, useEffect } from "react";
import axios from "axios"

const Home = () => {
  const [breweries, setBrewery] = useState([]);
  useEffect(() => {

    axios
      .get(`https://api.openbrewerydb.org/breweries`)
      .then(response => {
        console.log(response.data);
        setBrewery(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  return (
    <div className="brewerie-list">
      <h1>Search Breweries</h1>
      <input className="search" placeholder="search brew pubs"></input>
      <button type="submit">Submit</button>
      {breweries.map(name => (
        <div>{name.id}</div>

      ))}
    </div>
  );
};

export default Home;
