    // change ^^^ that line and grab the id from the URL
    // You will NEED to add a dependency array to this effect hook

    axios
      .get(`https://api.openbrewerydb.org/breweries`)
      .then(response => {
        console.log(response);
        setBrewery()
      })

      .catch(error => {

        console.error(error);
      });
  }, []);



  const setBars = () => {
    const bars =  breweries.map();
